context("init pop")if (interactive()) {test_that("simple num / int", {  ps = makeParamSet(    makeIntegerVectorParam("w", length=3, lower=7:9, upper=20),    makeIntegerParam("x", lower=0, upper=6),    makeNumericParam("u", lower=1, upper=4),    makeNumericVectorParam("v", length=2, lower=1:2, upper=10:11)  )  n = 10  ctrl = makeMiesControl(mu=n)  i = makeInternal(ps, ctrl)  pop = initPopulation(ps, ctrl, i)  expect_is(pop, "list")  expect_equal(length(pop), n)    for (i in 1:n) {    x = pop[[i]]    expect_is(x, "list")    expect_true(setequal(names(x), c("r", "z", "d", "sigma", "xi", "p")))    expect_true(is.numeric(x$r) && length(x$r) == 3)    expect_true(x$r[1] >= 1 && x$r[1] <= 4)    expect_true(x$r[2] >= 1 && x$r[2] <= 10)    expect_true(x$r[3] >= 2 && x$r[3] <= 11)    expect_true(is.integer(x$z) && length(x$z) == 4)    expect_true(x$z[1] >= 7 && x$z[1] <= 20)    expect_true(x$z[2] >= 8 && x$z[2] <= 20)    expect_true(x$z[3] >= 9 && x$z[3] <= 20)    expect_true(x$z[4] >= 0 && x$z[4] <= 6)    expect_equal(x$d, character(0))    expect_true(is.numeric(x$sigma) && length(x$sigma) == 3)    expect_true(is.numeric(x$xi) && length(x$xi) == 4)    expect_equal(x$p, numeric(0))  }})test_that("discrete", {  ps = makeParamSet(    makeDiscreteParam("u", values=c("a", "b")),    makeNumericParam("v", lower=1, upper=4),    makeDiscreteParam("w", values=list(c=list(), d=3))  )  n = 10  ctrl = makeMiesControl(mu=n)  i = makeInternal(ps, ctrl)  pop = initPopulation(ps, ctrl, i)  expect_is(pop, "list")  expect_equal(length(pop), n)    for (i in 1:n) {    x = pop[[i]]    expect_is(x, "list")    expect_true(setequal(names(x), c("r", "z", "d", "sigma", "xi", "p")))    expect_true(is.numeric(x$r) && length(x$r) == 1)    expect_true(x$r[1] >= 1 && x$r[1] <= 4)    expect_equal(x$z, integer(0))    expect_true(is.character(x$d) && length(x$d) == 2)    expect_true(x$d[1] %in% c("a", "b"))    expect_true(x$d[2] %in% c("c", "d"))    expect_true(is.numeric(x$sigma) && length(x$sigma) == 1)    expect_equal(x$xi, numeric(0))    expect_true(is.numeric(x$p) && length(x$p) == 2)  }})}